INSERT INTO TB_CRT_BASIC (
    CRT_ID
    , KOR_NAME
    , ENG_NAME
    , CREATE_DATE
    , MODIFIED_DATE
    , VERSION_NO
) VALUES (
    #{crt_id}
    , #{kor_name}
    , #{eng_name}
    , SYSDATE()
    , SYSDATE()
    , #{version_no}
)
ON DUPLICATE KEY UPDATE
    CRT_ID      = #{key},
    version_no  = #{version_no}

